from flask import Flask, request
from flask_cors import CORS
from readability import Document
from bs4 import BeautifulSoup
from model import summarize, summarize_all, simplify, simplify_all, q_a, match

app = Flask(__name__)
CORS(app)

@app.route('/send_text', methods=['POST'])
def action_router():
    data = request.json
    # Making sure data format is correct here
    try:
        highlighted = data['highlighted']  # Empty if you want full article, ignored if you send type 'qanda'
        article = data['article']  # Empty if you don't have anything stored in cookies
        raw_website = data['raw_website'] # Always need to send this, but it will be ignored if article isn't empty
        reqType = data['reqType'] # Send me either 'summarize' or 'simplify' or 'qanda'
        question = data['question']  # This will be blank/ignored, unless type is 'qanda'
    except:
        return 'Error, please send the data in correct format (make sure all vars are specified)'

    # Initialize the article of the article
    if article == '':
        article = scrape_text(raw_website)

    response = "DIDN'T SEND A PROPER REQTYPE"

    if reqType == 'summarize' and highlighted == '':
        print(1)
        response = summarize_all(article)
    elif reqType == 'summarize' and highlighted != '':
        print(2)
        response = summarize(article, highlighted)
    elif reqType == 'simplify' and highlighted == '':
        print(3)
        response = simplify_all(article)
    elif reqType == 'simplify' and highlighted != '':
        print(4)
        response = simplify(article, highlighted)
    elif reqType == 'question':
        print(5)
        response = q_a(article, question)

    words = response.split()
    matchingWords = set()
    for i, word in enumerate(words):
        if match(word, response, article, 0.5):
            matchingWords.add(word)

    return {
        'response': response,
        'article': article,
        'matching_words': list(matchingWords)
    }

def scrape_text(raw_website):
    doc = Document(raw_website)
    summary_html = doc.summary()
    soup = BeautifulSoup(summary_html, 'html.parser')
    txt = soup.get_text()
    return txt[0 : 5000]


